import { supabase } from "@/integrations/supabase/client";

// Base64 encoded placeholder images (small sample images)
const placeholderImages = {
  product1: "data:image/jpeg;base64,",
  product2: "data:image/jpeg;base64,",
  product3: "data:image/jpeg;base64,"
};

export const uploadPlaceholderImage = async (
  base64Data: string,
  fileName: string,
  vendorId: string
): Promise<string | null> => {
  try {
    // Convert base64 to blob
    const response = await fetch(base64Data);
    const blob = await response.blob();
    
    const filePath = `${vendorId}/seed-${fileName}.jpg`;
    
    const { error: uploadError } = await supabase.storage
      .from('product-images')
      .upload(filePath, blob, {
        contentType: 'image/jpeg',
        upsert: true
      });

    if (uploadError) {
      console.error('Error uploading placeholder image:', uploadError);
      return null;
    }

    // Get the public URL
    const { data } = supabase.storage
      .from('product-images')
      .getPublicUrl(filePath);

    return data.publicUrl;
  } catch (error) {
    console.error('Error in uploadPlaceholderImage:', error);
    return null;
  }
};

export const seedDummyProductsWithImages = async (storeId: string, vendorId: string) => {
  const dummyProductData = [
    {
      name: "Traditional Beaded Handbag",
      price: 4500,
      description: "Handmade African handbag made with colorful beads.",
      base64: placeholderImages.product1
    },
    {
      name: "Artisan Wooden Sculpture",
      price: 8200,
      description: "Beautiful hand-carved wooden sculpture by local artisans.",
      base64: placeholderImages.product2
    },
    {
      name: "Ankara Print Fabric",
      price: 3800,
      description: "High-quality Ankara fabric perfect for traditional wear.",
      base64: placeholderImages.product3
    }
  ];

  const productsToInsert = [];

  for (let i = 0; i < dummyProductData.length; i++) {
    const product = dummyProductData[i];
    const imageUrl = await uploadPlaceholderImage(product.base64, `product-${i + 1}`, vendorId);
    
    if (imageUrl) {
      productsToInsert.push({
        name: product.name,
        price: product.price,
        description: product.description,
        image_url: imageUrl,
        store_id: storeId,
        vendor_id: vendorId,
        status: 'active'
      });
    }
  }

  if (productsToInsert.length > 0) {
    const { error } = await supabase
      .from('products')
      .insert(productsToInsert);
    
    if (error) {
      console.error('Error seeding dummy products:', error);
      return false;
    }
    
    return true;
  }
  
  return false;
};